import Chart from "../Chart/Chart";

function ExpenseChart(props) {
    const ChartDataPoints=[
        {month:'january',amount:0},
        {month:'February',amount:0},
        {month:'March',amount:0},
        {month:'April',amount:0},
        {month:'May',amount:0},
        {month:'June',amount:0},
        {month:'July',amount:0},
        {month:'August',amount:0},
        {month:'September',amount:0},
        {month:'October',amount:0},
        {month:'November',amount:0},
        {month:'December',amount:0},
    ]
for(const expense of props.items){
   const expenseMonth = expense.date.getMonth(); // starting at 0 => January => 0
  ChartDataPoints[expenseMonth]+=expense.amount;
    }

    return (
        <Chart chartItems={ChartDataPoints}/>
    );
}
export default ExpenseChart;
